import React, {Component} from 'react';
import Nav from './components/Nav/Nav.js';
import Logo from './components/Logo/Logo.js';
import ImageForm from './components/ImageForm/ImageForm.js';
import Rank from './components/Rank/Rank.js';
import ParticlesBg from 'particles-bg';
import FaceRec from './components/FaceRec/FaceRec.js';
import SignIn from './components/SignIn/SignIn.js';
import SignUp from './components/SignUp/SignUp.js';
import './App.css';


const returnClarifaiRequestOptions =(imageUrl) => {
  const PAT = 'dac978f975774b93b6abb8f7755dc424';

  // Specify the correct user_id/app_id pairings

  // Since you're making inferences outside your app's scope
  const USER_ID = 'p354sp444d9v';
  const APP_ID = 'face-rec';
  // Change these to whatever model and image URL you want to use
  const MODEL_ID = 'face-detection';
  const IMAGE_URL = imageUrl;

  const raw = JSON.stringify({

    "user_app_id": {

        "user_id": USER_ID,

        "app_id": APP_ID

    },

    "inputs": [
        {
            "data": {
                "image": {
                    "url": IMAGE_URL
                }
            }
        }
    ]
  })

 const requestOptions = {

    method: 'POST',

    headers: {

        'Accept': 'application/json',

        'Authorization': 'Key ' + PAT

    },

    body: raw
  }
  return requestOptions;
}


class App extends Component {
  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box: {},
      route:'signIn',
      isSignedIn: false,
      user: {
        id: '',
			  name: '',
			  email: '',
			  entries:  0,
			  joined: '',
      }
    }
  }

  loadUser = (data) =>{
    this.setState({user: {
      id: data.id,
      name: data.name,
      email: data.email,
      entries:  data.entries,
      joined: data.joined,
    }
  })
  }


  calculateFaceLocation = (data) => {
    const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
    const image = document.getElementById('inputImage')
    const width = Number(image.width);
    const height = Number(image.height);
    return {
      leftCol: clarifaiFace.left_col * width,
      topRow:clarifaiFace.top_row * height,
      rightCol: width - (clarifaiFace.right_col * width),
      bottomRow: height - (clarifaiFace.bottom_row * height)
    }
  }

  displayFaceBox = (box) => {
    this.setState({box: box})
  }

  onInputChange = (event) => {
    this.setState({input: event.target.value});
  }

  onPictureSubmit = () => {
    this.setState({imageUrl: this.state.input});

    fetch("https://api.clarifai.com/v2/models/" + "face-detection" + "/outputs", returnClarifaiRequestOptions(this.state.input))
    .then(response => response.json())
    .then(result => {
      if(result){
        fetch('http://localhost:3030/image', {
            method: 'put',
            headers: {'content-type': 'application/json'},
            body: JSON.stringify({
                id: this.state.user.id
            })
        }).then(response => response.json()).then(data =>{
          console.log(data);
        }).catch9err
      }
      this.displayFaceBox(this.calculateFaceLocation(result))})
    .catch(error => console.log("error", error));
  }

  onRouteChange = (route) => {
    if(route === 'signOut') {
      this.setState({isSignedIn: false})
    } else if (route === 'home') {
      this.setState({isSignedIn: true})
    }
    this.setState({route: route})
  }


  render(){
    const {imageUrl, box, route, isSignedIn} = this.state;
    return (
      <div className='App'>
       <ParticlesBg type='cobweb' bg={true} />
        <Nav isSignedIn={isSignedIn} onRouteChange={this.onRouteChange}/>
        {
        route === 'home' ? 
        <div>
          <Logo />
          <Rank name={this.state.user.name} entries={this.state.user.entries}/>
          <ImageForm onInputChange = {this.onInputChange} onPictureSubmit={this.onPictureSubmit}/>
          <FaceRec box={box} imageUrl={imageUrl} />
        </div> : 
        (
          route === 'signIn' ?
          <SignIn loadUser={this.loadUser} onRouteChange ={this.onRouteChange} /> : <SignUp loadUser={this.loadUser} onRouteChange = {this.onRouteChange} />
        )

          }
      </div>
    )
  }
}

export default App;
