import React, {Component} from 'react';
import Nav from './components/Nav/Nav.js';
import Logo from './components/Logo/Logo.js';
import ImageForm from './components/ImageForm/ImageForm.js';
import Rank from './components/Rank/Rank.js';
import ParticlesBg from 'particles-bg';
import FaceRec from './components/FaceRec/FaceRec.js';
import './App.css';


const returnClarifaiRequestOptions =(imageUrl) => {
  const PAT = 'dac978f975774b93b6abb8f7755dc424';

  // Specify the correct user_id/app_id pairings

  // Since you're making inferences outside your app's scope
  const USER_ID = 'p354sp444d9v';
  const APP_ID = 'face-rec';
  // Change these to whatever model and image URL you want to use
  const MODEL_ID = 'face-detection';
  const IMAGE_URL = imageUrl;

  const raw = JSON.stringify({

    "user_app_id": {

        "user_id": USER_ID,

        "app_id": APP_ID

    },

    "inputs": [
        {
            "data": {
                "image": {
                    "url": IMAGE_URL
                }
            }
        }
    ]
  })

 const requestOptions = {

    method: 'POST',

    headers: {

        'Accept': 'application/json',

        'Authorization': 'Key ' + PAT

    },

    body: raw
  }
  return requestOptions;
}


class App extends Component {
  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box: {}
    }
  }

  calculateFaceLocation = (data) => {
    const clarifaiFace = data.outputs[0].dataregions[0].region_info.bounding_box;
    const image = document.getElementById('inputImage')
    const width = Number(image.width);
    const height = Number(image.height);
    console.log(width, height);
    return {
      leftcol: clarifaiFace.leftcol 
    }
  }

  onInputChange = (event) => {
    this.setState({input: event.target.value});
  }

  onButtonSubmit = () => {
    this.setState({imageUrl: this.state.input});

    fetch("https://api.clarifai.com/v2/models/" + "face-detection" + "/outputs", returnClarifaiRequestOptions(this.state.input))
    .then(response => response.json())
    .then(result => this.calculateFaceLocation(result))
    .catch(error => console.log("error", error));
  }

  /*response.outputs[0].data.regions[0].region_info.bounding_box*/

  render(){
    return (
      <div className='App'>
        <ParticlesBg type='cobweb' bg={true} />
        <Nav />
        <Logo />
        <Rank />
        <ImageForm onInputChange = {this.onInputChange} onButtonSubmit={this.onButtonSubmit}/>
        <FaceRec imageUrl= {this.state.imageUrl}/>
      </div>
    )
  }
}

export default App;
